export interface FunctionResult {

    /**
     *获取函数执行后返回的对象
     */
    getValue(): any;
}

export interface FunctionCallable {

    /**
     *函数的超时时间
     */
    timeout:number|undefined;

    /**
     *调用函数
     */
    call(reqBody?:any): Promise<FunctionResult>;

    /**
     *创建FunctionCallable实例，使用指定的超时时间
     */
    clone(timeout:number):FunctionCallable;
}

export interface AGConnectFunction {

    /**
     *通过函数的NOPATHHTTP触发器的URL设置调用哪个云函数
     */
    wrap(httpTriggerURI: string): FunctionCallable;
}
